mapper.insert:
INSERT INTO MEMBER ( MEMBER_NO, MEMBER_ID, MEMBER_PWD, MEMBER_NAME, MEMBER_PHONE, MEMBER_AGE, MEMBER_EMAIL, MEMBER_ADDR, MEMBER_DATE
) VALUES( MEMBER_SEQ.NEXTVAL, #{member_id}, #{member_pwd}, #{member_name}, #{member_phone}, #{member_age}, #{member_email}, #{member_addr}, sysdate
)

mapper.select:
SELECT * FROM MEMBER
WHERE
MEMBER_ID=#{member_id} AND MEMBER_PWD=#{member_pwd}

SELECT * FROM (
	SELECT ROWNUM AS RN, A.* FROM (
		SELECT * FROM MEMBER
		WHERE UPPER(MEMBER_NAME) LIKE UPPER('%'||#{sPrd}||'%') OR LOWER(MEMBER_NAME) LIKE LOWER('%'||#{sPrd}||'%')
	) A
) WHERE RN BETWEEN #{start} AND #{end}

mapper.update:
UPDATE MEMBER SET
MEMBER_PWD = #{member_pwd}
WHERE MEMBER_ID=#{member_id} AND MEMBER_NAME=#{member_name}
